package BasicMethods;

public class String_reverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Reverse is : ");
		String N="Devaraj";
		N.charAt(0);
		for(int i=N.length()-1; i>=0; i--)
		{
			System.out.print(" " +N.charAt(i));
		}
		
	}

}
